const deleteProductsBulkAction = {
  location: 'product-list',
  label: marker('common.delete'),
  icon: 'trash',
  iconClass: 'is-danger',
  requiresPermission: userPermissions => userPermissions.includes(Permission.DeleteProduct) || userPermissions.includes(Permission.DeleteCatalog),
  onClick: ({
    injector,
    selection,
    hostComponent,
    clearSelection
  }) => {
    const modalService = injector.get(ModalService);
    const dataService = injector.get(DataService);
    const notificationService = injector.get(NotificationService);
    modalService.dialog({
      title: marker('catalog.confirm-bulk-delete-products'),
      translationVars: {
        count: selection.length
      },
      buttons: [{
        type: 'secondary',
        label: marker('common.cancel')
      }, {
        type: 'danger',
        label: marker('common.delete'),
        returnValue: true
      }]
    }).pipe(switchMap(response => response ? dataService.product.deleteProducts(unique(selection.map(p => p.id))) : EMPTY)).subscribe(result => {
      let deleted = 0;
      const errors = [];
      for (const item of result.deleteProducts) {
        if (item.result === DeletionResult.DELETED) {
          deleted++;
        } else if (item.message) {
          errors.push(item.message);
        }
      }
      if (0 < deleted) {
        notificationService.success(marker('catalog.notify-bulk-delete-products-success'), {
          count: deleted
        });
      }
      if (0 < errors.length) {
        notificationService.error(errors.join('\n'));
      }
      hostComponent.refresh();
      clearSelection();
    });
  }
};
const assignProductsToChannelBulkAction = {
  location: 'product-list',
