        type: 'danger',
        label: marker('common.delete'),
        returnValue: true
      }]
    }).pipe(switchMap(response => response ? dataService.product.deleteProductVariants(unique(selection.map(p => p.id))) : EMPTY)).subscribe(result => {
      let deleted = 0;
      const errors = [];
